function validarRUT(rut) {
  rut = rut.replace(/^0+|[^0-9kK]+/g, '').toUpperCase();
  if (rut.length < 2) return false;

  const cuerpo = rut.slice(0, -1);
  const dv = rut.slice(-1);
  let suma = 0, multiplo = 2;

  for (let i = cuerpo.length - 1; i >= 0; i--) {
    suma += parseInt(cuerpo[i]) * multiplo;
    multiplo = multiplo < 7 ? multiplo + 1 : 2;
  }

  const dvEsperado = 11 - (suma % 11);
  const dvFinal = dvEsperado === 11 ? '0' : dvEsperado === 10 ? 'K' : dvEsperado.toString();

  return dv === dvFinal;
}

document.getElementById('registroForm').addEventListener('submit', function(e) {
  e.preventDefault();

  const nombre = document.getElementById('nombre').value;
  const rut = document.getElementById('rut').value;
  const correo = document.getElementById('correo').value;

  if (!validarRUT(rut)) {
    alert("RUT no válido. Verifica el formato.");
    return;
  }

  const preguntas = obtenerPreguntas();
  mostrarPreguntas(preguntas, nombre, rut, correo);
});


function mostrarPreguntas(preguntas, nombre, rut, correo) {
  const contenedor = document.getElementById('cuestionario');
  const form = document.createElement('form');
  let index = 0;

  [...preguntas.alternativas, ...preguntas.vf].forEach((pregunta) => {
    const div = document.createElement('div');
    div.className = 'pregunta';
    const tipo = pregunta[0].toLowerCase();
    const enunciado = pregunta[1];
    const opciones = tipo === 'alternativa' ? pregunta.slice(2, 6) : [pregunta[2], pregunta[3]];
    const correcta = pregunta[6];

    const p = document.createElement('p');
    p.innerHTML = `<strong>${enunciado}</strong>`;
    div.appendChild(p);

    opciones.forEach((opcion, i) => {
      const label = document.createElement('label');
      const input = document.createElement('input');
      input.type = 'radio';
      input.name = `q${index}`;
      input.value = tipo === 'alternativa' ? ['A', 'B', 'C', 'D'][i] : opcion.toUpperCase();
      label.appendChild(input);
      label.appendChild(document.createTextNode(` ${opcion}`));
      div.appendChild(label);
    });

    const inputCorrecta = document.createElement('input');
    inputCorrecta.type = 'hidden';
    inputCorrecta.name = `correct${index}`;
    inputCorrecta.value = correcta.toUpperCase();
    div.appendChild(inputCorrecta);

    form.appendChild(div);
    index++;
  });

  const boton = document.createElement('button');
  boton.type = 'submit';
  boton.textContent = 'Enviar respuestas';
  form.appendChild(boton);
  contenedor.innerHTML = '';
  contenedor.appendChild(form);
  contenedor.style.display = 'block';

  function normalizar(valor) {
    if (!valor) return "";
    const v = valor.toString().trim().toUpperCase();
    if (v === "V" || v === "VERDADERO") return "VERDADERO";
    if (v === "F" || v === "FALSO") return "FALSO";
    return v;
  }

  form.addEventListener('submit', function(e) {
    e.preventDefault();
    let correctas = 0;
    let respuestasIncorrectas = '';

    for (let i = 0; i < index; i++) {
      const seleccionada = form.querySelector(`input[name="q${i}"]:checked`);
      const correcta = form.querySelector(`input[name="correct${i}"]`).value;

      const preguntaTexto = form.querySelectorAll('div')[i].querySelector('p strong').innerText;

      let detalleUsuario = seleccionada ? seleccionada.nextSibling.textContent.trim() : 'Sin responder';
      let detalleCorrecta = correcta;

      if (normalizar(seleccionada?.value) === normalizar(correcta)) {
        correctas++;
      } else {
        respuestasIncorrectas += `
          <div style="margin-bottom: 10px; border-bottom: 1px solid #ccc; padding-bottom: 8px;">
            <strong style="display:block; margin-bottom: 5px;">Pregunta ${i + 1}:</strong>
            <div style="margin-left: 10px;">
              <div><strong>Texto:</strong> ${preguntaTexto}</div>
              <div><span style="color:red;"><strong>Incorrecta. Elegiste:</strong> ${detalleUsuario}</span></div>
              <div><span style="color:green;"><strong>Correcta:</strong> ${detalleCorrecta}</span></div>
            </div>
          </div>`;
      }
    }

    const puntaje = Math.round((correctas / index) * 100);
    contenedor.innerHTML = `
      <h2>Resultado Final: ${puntaje}%</h2>
      ${respuestasIncorrectas}
      <button onclick="location.reload()">Nuevo Intento</button>
    `;
  });
}
